g.script:
const svg = document.querySelector('#grid');
let size = 12, gap = 5;
for(let y=0;y<10;y++){
  for(let x=0;x<35;x++){
    let rect = document.createElementNS('http://www.w3.org/2000/svg','rect');
    rect.setAttribute('x', x*(size+gap)+40);
    rect.setAttribute('y', y*(size+gap)+40);
    rect.setAttribute('width', size);
    rect.setAttribute('height', size);
    rect.setAttribute('rx', 2);
    rect.setAttribute('fill', `hsl(${270 + Math.random()*20}, 80%, ${30+Math.random()*50}%)`);
    rect.style.animation = `pulse ${1+Math.random()*2}s infinite alternate`;
    svg.appendChild(rect);
  }
}
